namespace RTProClientToolsMac.Controllers;public class Configurations(IConfiguration configuration){
    private const string CLIENT_TOOLS_TEMP_FOLDER_NAME = "ClientToolsTemp";
    public string? CopyPrintPath { get; } = configuration.GetSection("CopyPrintPath").Value;    public string TempDirectory    {        get        {            var tempPath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData), CLIENT_TOOLS_TEMP_FOLDER_NAME);            Directory.CreateDirectory(tempPath);            return tempPath;        }    }    public TimeSpan KeepTempFilesFor { get; } = TimeSpan.FromDays(1);}